INSERT INTO `sach` (`masach`, `tensach`, `anhdaidien`, `tinhtrang_full`, `tacgia`, `tomtat`, `theloai`, `kiemduyet`, `luotxem`) VALUES
(1, 'Tôi thấy hoa vàng trên cỏ xanh', 'images/hinh1.jpg', 0, 1, '<p>T&aacute;c phẩm như một tập nhật k&yacute; xoay quanh cuộc sống của những đứa trẻ ở một v&ugrave;ng qu&ecirc; Việt Nam ngh&egrave;o kh&oacute;, nổi bật l&ecirc;n l&agrave; th&ocirc;ng điệp về t&igrave;nh anh em, t&igrave;nh l&agrave;ng nghĩa x&oacute;m v&agrave; những t&acirc;m tư của tuổi mới lớn. Theo Nguyễn Nhật &Aacute;nh, đ&acirc;y l&agrave; lần đầu ti&ecirc;n &ocirc;ng đưa v&agrave;o truyện của m&igrave;nh những nh&acirc;n vật phản diện, đặt ra vấn đề đạo đức như sự v&ocirc; t&acirc;m hay c&aacute;i &aacute;c.</p>\r\n', 2, 1, 8),
(2, 'Sài Gòn-Chợ Lớn', 'images/hinh2.jpg', 0, 6, 'Đại diện Nhà xuất bản (NXB) Văn Hóa - Văn Nghệ cho biết, qua nhiều tài liệu báo chí đương thời tác phẩm “Sài Gòn – Chợ Lớn: Thể thao và báo chí trước 1945” đã phác họa lại những nét đặc sắc về lịch sử thể thao và báo chí Sài Gòn - Chợ Lớn trước năm 1945. Ví dụ như môn bóng đá và đội bóng Ngôi sao Gia Định, môn quần vợt với những tay vợt nổi danh một thời như Nguyễn Văn Chim, Huỳnh Văn Giao… Đua xe đạp với các cua-rơ: “phụng hoàng” Lê Thành Các, “thần mã” Vũ Văn Thân, “vua nước rút” Nguyễn Thành Phương… và một số môn thể thao khác như đua ngựa, quyền anh, hàng không, săn bắn…', 3, 0, 2),
(5, 'Đắc Nhân Tâm', 'images/dacnhantam.jpg', 0, 7, '<p><em><strong>Đắc nh&acirc;n t&acirc;m&nbsp;</strong>&ndash;&nbsp;<strong>How to win friends and Influence People&nbsp;</strong>&nbsp;của Dale Carnegie l&agrave; quyển s&aacute;ch nổi tiếng nhất, b&aacute;n chạy nhất v&agrave; c&oacute; tầm ảnh hưởng nhất của mọi thời đại. T&aacute;c phẩm đ&atilde; được chuyển ngữ sang hầu hết c&aacute;c thứ tiếng tr&ecirc;n thế giới v&agrave; c&oacute; mặt ở h&agrave;ng trăm quốc gia.&nbsp;</em></p>\r\n\r\n<p><em>Đ&acirc;y l&agrave; quyển s&aacute;ch duy nhất về thể loại self-help li&ecirc;n tục đứng đầu danh mục s&aacute;ch b&aacute;n chạy nhất (best-selling Books) do b&aacute;o The New York Times b&igrave;nh chọn suốt 10 năm liền. Ri&ecirc;ng bản tiếng Anh của s&aacute;ch đ&atilde; b&aacute;n được hơn 15 triệu bản tr&ecirc;n thế giới. T&aacute;c phẩm c&oacute; sức lan toả v&ocirc; c&ugrave;ng rộng lớn &ndash; d&ugrave; bạn đi đến bất cứ nơi đ&acirc;u, bất kỳ quốc gia n&agrave;o cũng đều c&oacute; thể nh&igrave;n thấy. T&aacute;c phẩm được đ&aacute;nh gi&aacute; l&agrave; quyển s&aacute;ch đầu ti&ecirc;n v&agrave; hay nhất, c&oacute; ảnh hưởng l&agrave;m thay đổi cuộc đời của h&agrave;ng triệu người tr&ecirc;n thế giới.</em></p>\r\n\r\n<p>Kh&ocirc;ng c&ograve;n nữa kh&aacute;i niệm giới hạn,&nbsp;<strong>Đắc Nh&acirc;n T&acirc;m</strong>&nbsp;l&agrave; nghệ thuật thu phục l&ograve;ng người, l&agrave; l&agrave;m cho tất cả mọi người y&ecirc;u mến m&igrave;nh.&nbsp;<strong>Đắc nh&acirc;n t&acirc;m</strong>&nbsp;v&agrave; c&aacute;i T&agrave;i trong mỗi người ch&uacute;ng ta.&nbsp;<strong>Đắc Nh&acirc;n T&acirc;m&nbsp;</strong>trong &yacute; nghĩa đ&oacute; cần được thụ đắc bằng sự hiểu r&otilde; bản th&acirc;n, th&agrave;nh thật với ch&iacute;nh m&igrave;nh, hiểu biết v&agrave; quan t&acirc;m đến những người xung quanh để nh&igrave;n ra v&agrave; khơi gợi những tiềm năng ẩn khuất nơi họ, gi&uacute;p họ ph&aacute;t triển l&ecirc;n một tầm cao mới. Đ&acirc;y ch&iacute;nh l&agrave; nghệ thuật cao nhất về con người v&agrave; ch&iacute;nh l&agrave; &yacute; nghĩa s&acirc;u sắc nhất đ&uacute;c kết từ những nguy&ecirc;n tắc v&agrave;ng của Dale Carnegie.</p>\r\n\r\n<p>Quyển s&aacute;ch&nbsp;<strong>Đắc nhắn t&acirc;m</strong>&nbsp;l&agrave; cuốn s&aacute;ch &ldquo;đầu ti&ecirc;n v&agrave; hay nhất mọi thời đại về nghệ thuật giao tiếp v&agrave; ứng xử&rdquo;, quyển s&aacute;ch đ&atilde; từng mang đến th&agrave;nh c&ocirc;ng v&agrave; hạnh ph&uacute;c cho h&agrave;ng triệu người tr&ecirc;n khắp thế giới.</p>\r\n\r\n<p><strong>Đắc Nh&acirc;n T&acirc;m</strong>&nbsp;l&agrave; cuốn s&aacute;ch gối đầu giường về đối nh&acirc;n xử thế.&nbsp;&nbsp;</p>\r\n', 4, 1, 0),
(6, 'Cho tôi xin một vé đi tuổi thơ', 'images/ChoToiXinMotVeDiTuoiTho.jpg', 0, 1, '<p>Năm đ&oacute; t&ocirc;i t&aacute;m tuổi. Sau n&agrave;y, t&ocirc;i cũng nhiều lần thấy cuộc sống đ&aacute;ng ch&aacute;n khi thi trượt ở tuổi mười lăm, thất t&igrave;nh ở tuổi hăm bốn, thất nghiệp ở tuổi ba mươi ba v&agrave; gặt h&aacute;i mọi th&agrave;nh c&ocirc;ng ở tuổi bốn mươi. Nhưng t&aacute;m tuổi c&oacute; c&aacute;i buồn ch&aacute;n của tuổi l&ecirc;n t&aacute;m. &ETH;&oacute; l&agrave; c&aacute;i ng&agrave;y kh&ocirc;ng hiểu sao t&ocirc;i lại c&oacute; &yacute; nghĩ rằng cuộc sống kh&ocirc;ng c&oacute; g&igrave; để m&agrave; chờ đợi nữa.</p>\r\n\r\n<p>Rất nhiều năm về sau, t&ocirc;i được biết c&aacute;c triết gia v&agrave; c&aacute;c nh&agrave; thần học vẫn đang loay hoay đi t&igrave;m &yacute; nghĩa của cuộc sống v&agrave; tới Tết Ma Rốc họ cũng chưa chắc đ&atilde; t&igrave;m ra.Nhưng năm t&ocirc;i t&aacute;m tuổi, t&ocirc;i đ&atilde; thấy cuộc sống chả c&oacute; g&igrave; mới mẻ để kh&aacute;m ph&aacute;. Vẫn &aacute;nh mặt trời đ&oacute; chiếu rọi mỗi ng&agrave;y. Vẫn bức m&agrave;n đen đ&oacute; bu&ocirc;ng xuống mỗi đ&ecirc;m. Tr&ecirc;n m&aacute;i nh&agrave; v&agrave; tr&ecirc;n c&aacute;c c&agrave;nh l&aacute; sau vườn, gi&oacute; vẫn than thở giọng của gi&oacute;. Chim vẫn h&oacute;t giọng của chim. Dế ri ri giọng dế, g&agrave; quang qu&aacute;c giọng g&agrave;. N&oacute;i t&oacute;m lại, cuộc sống thật l&agrave; cũ kỹ.</p>\r\n\r\n<p>Cuộc sống của t&ocirc;i c&ograve;n cũ kỹ hơn nữa. Mỗi đ&ecirc;m, trước khi đi ngủ, t&ocirc;i đ&atilde; biết tỏng ng&agrave;y mai những sự kiện g&igrave; sẽ diễn ra trong cuộc đời t&ocirc;i. T&ocirc;i kể ra nh&eacute;: S&aacute;ng, t&ocirc;i phải cố hết sức để thức dậy trong khi t&ocirc;i vẫn c&ograve;n muốn ngủ tiếp. Tất nhi&ecirc;n l&agrave; trước đ&oacute; t&ocirc;i vẫn giả vờ ngủ m&ecirc; mặc cho mẹ t&ocirc;i k&ecirc;u khản cả giọng rồi lay lay người t&ocirc;i, nhưng dĩ nhi&ecirc;n t&ocirc;i vẫn trơ ra như kh&uacute;c gỗ cho đến khi mẹ t&ocirc;i c&ugrave; v&agrave;o l&ograve;ng b&agrave;n ch&acirc;n t&ocirc;i.</p>\r\n', 2, 1, 0);

