INSERT INTO `sach` (`masach`, `tensach`, `anhdaidien`, `tinhtrang_full`, `tacgia`, `tomtat`, `theloai`, `kiemduyet`, `luotxem`) VALUES
(1, 'Tôi thấy hoa vàng trên cỏ xanh', 'hinh1.jpg', 1, 1, '<p>Tác phẩm như một tập nhật ký xoay quanh cuộc sống của những đứa trẻ ở một vùng quê Việt Nam nghèo khó, nổi bật lên là thông điệp về tình anh em, tình làng nghĩa xóm và những tâm tư của tuổi mới lớn. Theo Nguyễn Nhật Ánh, đây là lần đầu tiên ông đưa vào truyện của mình những nhân vật phản diện, đặt ra vấn đề đạo đức như sự vô tâm hay cái ác.</p>\r\n', 2, 1, 39),
(2, 'Sài Gòn-Chợ Lớn, Ký ức đô thị và con người', 'hinh2.jpg', 0, 6, 'Đại diện Nhà xuất bản (NXB) Văn Hóa - Văn Nghệ cho biết, qua nhiều tài liệu báo chí đương thời tác phẩm “Sài Gòn – Chợ Lớn: Thể thao và báo chí trước 1945” đã phác họa lại những nét đặc sắc về lịch sử thể thao và báo chí Sài Gòn - Chợ Lớn trước năm 1945. Ví dụ như môn bóng đá và đội bóng Ngôi sao Gia Định, môn quần vợt với những tay vợt nổi danh một thời như Nguyễn Văn Chim, Huỳnh Văn Giao… Đua xe đạp với các cua-rơ: “phụng hoàng” Lê Thành Các, “thần mã” Vũ Văn Thân, “vua nước rút” Nguyễn Thành Phương… và một số môn thể thao khác như đua ngựa, quyền anh, hàng không, săn bắn…', 3, 0, 2),
(5, 'Đắc Nhân Tâm', 'dacnhantam.jpg', 0, 7, '<p><em><strong>Đắc nh&acirc;n t&acirc;m&nbsp;</strong>&ndash;&nbsp;<strong>How to win friends and Influence People&nbsp;</strong>&nbsp;của Dale Carnegie l&agrave; quyển s&aacute;ch nổi tiếng nhất, b&aacute;n chạy nhất v&agrave; c&oacute; tầm ảnh hưởng nhất của mọi thời đại. T&aacute;c phẩm đ&atilde; được chuyển ngữ sang hầu hết c&aacute;c thứ tiếng tr&ecirc;n thế giới v&agrave; c&oacute; mặt ở h&agrave;ng trăm quốc gia.&nbsp;</em></p>\r\n\r\n<p><em>Đ&acirc;y l&agrave; quyển s&aacute;ch duy nhất về thể loại self-help li&ecirc;n tục đứng đầu danh mục s&aacute;ch b&aacute;n chạy nhất (best-selling Books) do b&aacute;o The New York Times b&igrave;nh chọn suốt 10 năm liền. Ri&ecirc;ng bản tiếng Anh của s&aacute;ch đ&atilde; b&aacute;n được hơn 15 triệu bản tr&ecirc;n thế giới. T&aacute;c phẩm c&oacute; sức lan toả v&ocirc; c&ugrave;ng rộng lớn &ndash; d&ugrave; bạn đi đến bất cứ nơi đ&acirc;u, bất kỳ quốc gia n&agrave;o cũng đều c&oacute; thể nh&igrave;n thấy. T&aacute;c phẩm được đ&aacute;nh gi&aacute; l&agrave; quyển s&aacute;ch đầu ti&ecirc;n v&agrave; hay nhất, c&oacute; ảnh hưởng l&agrave;m thay đổi cuộc đời của h&agrave;ng triệu người tr&ecirc;n thế giới.</em></p>\r\n\r\n<p>Kh&ocirc;ng c&ograve;n nữa kh&aacute;i niệm giới hạn,&nbsp;<strong>Đắc Nh&acirc;n T&acirc;m</strong>&nbsp;l&agrave; nghệ thuật thu phục l&ograve;ng người, l&agrave; l&agrave;m cho tất cả mọi người y&ecirc;u mến m&igrave;nh.&nbsp;<strong>Đắc nh&acirc;n t&acirc;m</strong>&nbsp;v&agrave; c&aacute;i T&agrave;i trong mỗi người ch&uacute;ng ta.&nbsp;<strong>Đắc Nh&acirc;n T&acirc;m&nbsp;</strong>trong &yacute; nghĩa đ&oacute; cần được thụ đắc bằng sự hiểu r&otilde; bản th&acirc;n, th&agrave;nh thật với ch&iacute;nh m&igrave;nh, hiểu biết v&agrave; quan t&acirc;m đến những người xung quanh để nh&igrave;n ra v&agrave; khơi gợi những tiềm năng ẩn khuất nơi họ, gi&uacute;p họ ph&aacute;t triển l&ecirc;n một tầm cao mới. Đ&acirc;y ch&iacute;nh l&agrave; nghệ thuật cao nhất về con người v&agrave; ch&iacute;nh l&agrave; &yacute; nghĩa s&acirc;u sắc nhất đ&uacute;c kết từ những nguy&ecirc;n tắc v&agrave;ng của Dale Carnegie.</p>\r\n\r\n<p>Quyển s&aacute;ch&nbsp;<strong>Đắc nhắn t&acirc;m</strong>&nbsp;l&agrave; cuốn s&aacute;ch &ldquo;đầu ti&ecirc;n v&agrave; hay nhất mọi thời đại về nghệ thuật giao tiếp v&agrave; ứng xử&rdquo;, quyển s&aacute;ch đ&atilde; từng mang đến th&agrave;nh c&ocirc;ng v&agrave; hạnh ph&uacute;c cho h&agrave;ng triệu người tr&ecirc;n khắp thế giới.</p>\r\n\r\n<p><strong>Đắc Nh&acirc;n T&acirc;m</strong>&nbsp;l&agrave; cuốn s&aacute;ch gối đầu giường về đối nh&acirc;n xử thế.&nbsp;&nbsp;</p>\r\n', 4, 1, 5),
(6, 'Cho tôi xin một vé đi tuổi thơ', 'ChoToiXinMotVeDiTuoiTho.jpg', 0, 1, '<p>Năm đó tôi tám tuổi. Sau này, tôi cũng nhiều lần thấy cuộc sống đáng chán khi thi trượt ở tuổi mười lăm, thất tình ở tuổi hăm bốn, thất nghiệp ở tuổi ba mươi ba và gặt hái mọi thành công ở tuổi bốn mươi. Nhưng tám tuổi có cái buồn chán của tuổi lên tám. Ðó là cái ngày không hiểu sao tôi lại có ý nghĩ rằng cuộc sống không có gì để mà chờ đợi nữa.</p>\r\n\r\n<p>Rất nhiều năm về sau, tôi được biết các triết gia và các nhà thần học vẫn đang loay hoay đi tìm ý nghĩa của cuộc sống và tới Tết Ma Rốc họ cũng chưa chắc đã tìm ra.Nhưng năm tôi tám tuổi, tôi đã thấy cuộc sống chả có gì mới mẻ để khám phá. Vẫn ánh mặt trời đó chiếu rọi mỗi ngày. Vẫn bức màn đen đó buông xuống mỗi đêm. Trên mái nhà và trên các cành lá sau vườn, gió vẫn than thở giọng của gió. Chim vẫn hót giọng của chim. Dế ri ri giọng dế, gà quang quác giọng gà. Nói tóm lại, cuộc sống thật là cũ kỹ.</p>\r\n\r\n<p>Cuộc sống của tôi còn cũ kỹ hơn nữa. Mỗi đêm, trước khi đi ngủ, tôi đã biết tỏng ngày mai những sự kiện gì sẽ diễn ra trong cuộc đời tôi. Tôi kể ra nhé: Sáng, tôi phải cố hết sức để thức dậy trong khi tôi vẫn còn muốn ngủ tiếp. Tất nhiên là trước đó tôi vẫn giả vờ ngủ mê mặc cho mẹ tôi kêu khản cả giọng rồi lay lay người tôi, nhưng dĩ nhiên tôi vẫn trơ ra như khúc gỗ cho đến khi mẹ tôi cù vào lòng bàn chân tôi.</p>\r\n', 2, 1, 0),
(7, 'Tuyển tập Nam Cao', 'nhasachmienphi-tuyen-tap-nam-cao.jpg', 0, 8, 'Đời văn Nam Cao không dài. Trên mười năm sáng tác ở cả hai thời kỳ trước và sau Cách mạng, Nam Cao đã để lại nhiều tác phẩm có giá trị. Thời gian trôi qua, tác phẩm của Nam Cao như càng bộc lộ thêm những phẩm chất mới, những giá trị mới. Chúng ta càng thêm yêu quý ông, một tâm hồn trung thực và cao đẹp trong cuộc đời cũng như trên trang sách; một nhà văn chiến sĩ với ý nghĩa đích thực và trọn vẹn của danh hiệu này.', 2, 1, 0),
(8, 'Truyện Kể Tây Tạng', 'nhasachmienphi-truyen-ke-tay-tang.jpg', 0, 10, 'Truyện kể Tây Tạng là tuyển tập gồm 39 truyện dân gian Tây Tạng được lưu truyền hàng ngàn năm trong ký ức dân gian. Mỗi câu chuyện là một huyền thoại đẹp, một sự tích hay một ngụ ngôn về những thói tật của con người… nhưng cuối cùng, điều đọng lại trong lòng người đọc là khát vọng hướng thiện và bản sắc Tây Tạng đậm nét trong mỗi truyện kể.', 6, 1, 0);

